property.code:
(function () {
  'use strict';
  require.config({
    paths: {
      'moment-jdateformatparser': "$services.webjars.url('moment-jdateformatparser', 'moment-jdateformatparser.min')"
    }
  });
  const $ = jQuery;
  const taskDateFormat = "$!services.datemacro.configuration.storageDateFormat";
  const taskMacroName = "$!services.localization.render('rendering.macro.task.name')";
  const taskMacroDescription = "$!services.localization.render('rendering.macro.task.description')";
  const dateMacroName = "$!services.localization.render('rendering.macro.date.name')";
  const dateMacroDescription = "$!services.localization.render('rendering.macro.date.description')";
  const macroContentPlaceholder = "$!services.localization.render('ckeditor.plugin.task-insert.placeholder')";
  const refGenURL = XWiki.currentDocument.getRestURL() + '/task-reference';
  CKEDITOR.plugins.add('xwiki-task-insert', {
    init: function (editor) {
      require(['xwiki-meta', 'moment', 'moment-jdateformatparser'], function (xm, moment) {
        editor.addCommand('insert-prefilled-date-macro', {
          exec: function (editor) {
            var dateFormat = moment().toMomentFormatString(taskDateFormat);
            var currentDate = moment(new Date()).format(dateFormat);
            editor.execCommand('xwiki-macro-insert', {
              name: 'date',
              inline: true,
              parameters: {
                value: currentDate
              }
            });
          }
        });
        editor.addCommand('insert-prefilled-task-macro', {
          exec: function (editor) {
            let taskReference = '/Tasks/Task_' + Date.now();
            let creatorValue = xm.userReference.relativeTo(new XWiki.WikiReference(XWiki.currentWiki)).toString();
            var dateFormat = moment().toMomentFormatString(taskDateFormat);
            var createDateParam = moment(new Date()).format(dateFormat);
            $.ajax({
              url: refGenURL,
              method: 'GET',
              dataType: 'text'
            })
              .done(function (ref) {
                taskReference = ref;
              })
              .always(function () {
                editor.execCommand('xwiki-macro-insert', {
                  name: 'task',
                  inline: false,
                  parameters: {
                    reference: taskReference,
                    createDate: createDateParam,
                    reporter: creatorValue
                  },
                  content: macroContentPlaceholder
                });
              });
          }
        });
      });
    },
    afterInit: function (editor) {
      if (!editor.quickActions) {
        return;
      }
      editor.quickActions.addAction({
        group: 'Content',
        id: 'macro-task',
        name: taskMacroName,
        iconClass: 'fa fa-align-left',
        description: taskMacroDescription,
        command: {
          name: 'insert-prefilled-task-macro'
        }
      });
      editor.quickActions.addAction({
        group: 'Content',
        id: 'macro-date',
        name: dateMacroName,
        iconClass: 'fa fa-align-left',
        description: dateMacroDescription,
        command: {
          name: 'insert-prefilled-date-macro'
        }
      });
    }
  });
})();
